create table orders.deferred_order_details (order_id varchar(255) not null, callback_url varchar(255), created_timestamp datetime(6), is_pre_fulfilled bit, order_status varchar(255), deferred_until_date datetime(6), updated_timestamp datetime(6), valid_until_date datetime(6), primary key (order_id)) engine=InnoDB
create table orders.order_error (order_id varchar(255) not null, created_timestamp datetime(6), payment_partner varchar(255), plan_id integer, status_code varchar(255), order_type varchar(255), uid varchar(255), primary key (order_id)) engine=InnoDB
create table orders.orders (order_id varchar(255) not null, amount double precision, created_timestamp datetime(6), currency varchar(255), payment_partner varchar(255), partner_order_reference varchar(255) not null, payment_code varchar(255), plan_id integer, order_state varchar(255), order_type varchar(255), uid varchar(255), updated_timestamp datetime(6), primary key (order_id)) engine=InnoDB
create index partner_order_reference_index on orders (partner_order_reference)